import React, { useRef } from 'react';
import './Deposit.css'; // Ensure that the path to your CSS file is correct
import { useNavigate , useParams } from 'react-router-dom';

function Deposit() {
    const navigate = useNavigate();
    const { groupID } = useParams();
    const amountRef = useRef(null);
  // You would add state and event handlers here as needed
  const handleBack = () => {
    navigate(-1);
  };

  const deposit = async (depositAmount, groupID) => {
    // Check if depositAmount is null or empty
    if (!depositAmount || depositAmount.trim() === '') {
        console.log("Value cannot be empty");
    }

    if (depositAmount < 0){
      console.log("Value cannot be negative");
    }
    let data = null;

    // Check if depositAmount is a valid number
    if (!/^\d+(\.\d+)?$/.test(depositAmount.trim())) {
        throw new Error("Value is not a number");
    }

    try {  
        const data = await fetch(`http://localhost:5050/find/user/group/${groupID}`);
        const json = await data.json();
        const initialBalance = parseFloat(json.groups[0].balance);
        if (initialBalance < parseFloat(depositAmount)){
            throw new Error("Not enough balance in your account");
        }
        try {
            const response = await fetch(`http://localhost:5050/update/balance/${json.groups[0]._id}`, {
                method : "PATCH",
                headers : {
                    "Content-type" : "application/json"
                },
                body: JSON.stringify({
                    "balance" : initialBalance + parseFloat(depositAmount)
                })
            });
            if (!response.ok){
                throw new Error("Deposit unsuccessful");
            }
        } catch (error) {
            console.log(error);
        }

    } catch (error) {
        throw new Error(error);
    }
};



  return (
    <div className="deposit-wrapper">
      <div className="deposit-event">
        <h2>Deposit</h2>
        <form className="deposit-form">
          <div className="form-group">
            <label htmlFor="amount">Amount</label>
            <input type="number" id="amount" name="amount" ref={amountRef} required />
          </div>
          <button type="submit" className="deposit-button" onClick={deposit(amountRef, groupID)}>Add Deposit</button>
        </form>
        <button className="back-btn" onClick={handleBack}>Back</button>
      </div>
    </div>
  );
}

export default Deposit;
